mapper.select:
SELECT
from
( SELECT r.TABLE_NAME as TABLE_NAME, r.TABLE_SCHEMA as table_schema, r.COLUMN_NAME, r.DATA_TYPE, r.column_type, ifnull(r.extra,'') extra, ifnull(ifnull(r.CHARACTER_MAXIMUM_LENGTH,CONCAT(r.NUMERIC_PRECISION,'.',r.NUMERIC_SCALE)),'') as data_length, ifnull(r.column_default,'') as default_value, ifnull(r.COLUMN_COMMENT,'') comments, if(r.column_key='PRI','yes','no') as primary_key, if(r.is_NULLABLE='YES','yes','no') as null_able, if(r.column_key='PRI','true','false') as is_primary_key FROM information_schema.`COLUMNS` r WHERE r.TABLE_SCHEMA = #{t.tableSchema} AND r.TABLE_NAME = #{t.tableName}
)t
